import React, { useState } from 'react';

function Search() {
  const [rooms, setRooms] = useState([]);
  const [checkin, setCheckin] = useState('');
  const [checkout, setCheckout] = useState('');

  const handleSearch = () => {
    const url = new URL('http://localhost:3001/search/available');
    url.search = new URLSearchParams({ checkin, checkout }).toString();

    fetch(url)
      .then(response => response.json())
      .then(data => {
        setRooms(data);
        console.log('Available rooms:', data);
      })
      .catch(error => {
        console.error('Error fetching available rooms:', error);
      });
  };

  // Styles
  const containerStyle = {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
  };

  const buttonStyle = {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    marginBottom: '20px',
  };

  const inputStyle = {
    padding: '10px',
    margin: '0 10px 20px 0',
    borderRadius: '5px',
    border: '1px solid #ddd',
  };

  const roomStyle = {
    border: '1px solid #ddd',
    borderRadius: '5px',
    padding: '15px',
    marginBottom: '10px',
    backgroundColor: '#f9f9f9',
  };

  const titleStyle = {
    textAlign: 'center',
    color: '#333',
  };

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>Room Search</h2>
      <input
        type="date"
        value={checkin}
        onChange={e => setCheckin(e.target.value)}
        style={inputStyle}
      />
      <input
        type="date"
        value={checkout}
        onChange={e => setCheckout(e.target.value)}
        style={inputStyle}
      />
      <button onClick={handleSearch} style={buttonStyle}>Search Available Rooms</button>
      <div>
        {rooms.map(room => (
          <div key={room.Room_ID} style={roomStyle}>
            <p><strong>Room ID:</strong> {room.Room_ID}</p>
            <p><strong>Hotel ID:</strong> {room.Hotel_ID}</p>
            <p><strong>Price:</strong> ${room.Price}</p>
            <p><strong>Amenities:</strong> {room.Amenities}</p>
            <p><strong>Capacity:</strong> {room.Capacity}</p>
            <p><strong>Sea View:</strong> {room.Sea_View ? 'Yes' : 'No'}</p>
            <p><strong>Mountain View:</strong> {room.Mountain_View ? 'Yes' : 'No'}</p>
            <p><strong>Extendable:</strong> {room.Extendable ? 'Yes' : 'No'}</p>
            <p><strong>Damage Status:</strong> {room.Damage_Status ? 'Yes' : 'No'}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Search;
