import React, { useState } from 'react';

function Search() {
  const [rooms, setRooms] = useState([]);
  const [checkin, setCheckin] = useState('');
  const [checkout, setCheckout] = useState('');
  const [capacity, setCapacity] = useState(''); // Default empty, will be treated as 0 if not specified
  const [rating, setRating] = useState(''); // Default empty, will be treated as any rating if not specified

  const handleSearch = () => {
    // Check if either check-in or check-out dates are not filled out
    if (!checkin || !checkout) {
      alert('Please select both check-in and check-out dates.');
      return;
    }

    // Ensure check-in date is not after check-out date
    if (new Date(checkin) > new Date(checkout)) {
      alert('Check-in date cannot be after check-out date.');
      return;
    }

    // Construct the URL with query parameters for the search
    const url = new URL('http://localhost:3001/search');
    url.searchParams.append('checkin', checkin);
    url.searchParams.append('checkout', checkout);
    if (capacity) url.searchParams.append('capacity', capacity);
    if (rating) url.searchParams.append('rating', rating);

    // Fetch available rooms from the server
    fetch(url)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setRooms(data);
      })
      .catch(error => {
        console.error('Error fetching available rooms:', error);
      });
  };

  // Styles
  // ... (keep your existing styles here)

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>Room Search</h2>
      <input
        type="date"
        value={checkin}
        onChange={e => setCheckin(e.target.value)}
        style={inputStyle}
      />
      <input
        type="date"
        value={checkout}
        onChange={e => setCheckout(e.target.value)}
        style={inputStyle}
      />
      <input
        type="number"
        placeholder="Capacity"
        value={capacity}
        onChange={e => setCapacity(e.target.value)}
        min="0" // Minimum capacity set to 0
        style={inputStyle}
      />
      <input
        type="number"
        placeholder="Hotel Rating (1-5)"
        value={rating}
        onChange={e => setRating(e.target.value)}
        min="1" // Minimum rating set to 1
        max="5" // Maximum rating set to 5
        style={inputStyle}
      />
      <button onClick={handleSearch} style={buttonStyle}>
        Search Available Rooms
      </button>
      <div>
        {/* Map through rooms and display them */}
        {/* ... */}
      </div>
    </div>
  );
}

export default Search;
