import React, { useState } from 'react';

function Search() {
  const [searchResults, setSearchResults] = useState([]);

  // Example function to fetch search results
  const fetchSearchResults = () => {
    fetch('http://localhost:3001/api/rooms/search', {
      method: 'POST', // or 'GET' if your server is set up for it
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        /* criteria like date, capacity, etc. */
      }),
    })
    .then(response => response.json())
    .then(data => setSearchResults(data))
    .catch(error => console.error('Error:', error));
  };

  return (
    <div>
      <h2>Room Search</h2>
      {/* Add your search form here */}
      <button onClick={fetchSearchResults}>Search</button>
      <ul>
        {searchResults.map(room => (
          <li key={room.id}>{room.name} - {room.price}</li>
        ))}
      </ul>
    </div>
  );
}

export default Search;
