import React, { useState, useEffect } from 'react';

const HotelDirectory = () => {
  const [availableRooms, setAvailableRooms] = useState([]);
  const [hotels, setHotels] = useState([]);
  const [selectedHotelId, setSelectedHotelId] = useState('');
  const [hotelCapacity, setHotelCapacity] = useState({});

  // Fetch available rooms per area
  useEffect(() => {
    fetch('http://localhost:3001/available-rooms-per-area')
      .then(response => response.json())
      .then(data => setAvailableRooms(data))
      .catch(error => console.error('Error fetching available rooms per area:', error));
  }, []);

  // Fetch the list of hotels
  useEffect(() => {
    fetch('http://localhost:3001/hotels')
      .then(response => response.json())
      .then(data => setHotels(data))
      .catch(error => console.error('Error fetching hotels:', error));
  }, []);

  // Fetch hotel capacity when a hotel is selected
  useEffect(() => {
    if (selectedHotelId) {
      fetch(`http://localhost:3001/hotel-capacity?hotelId=${encodeURIComponent(selectedHotelId)}`)
        .then(response => response.json())
        .then(data => setHotelCapacity(data))
        .catch(error => console.error('Error fetching hotel capacity:', error));
    }
  }, [selectedHotelId]);

  return (
    <div>
      <h2>Hotel Directory</h2>

      {/* View 1: Available Rooms Per Area */}
      <section>
        <h3>Available Rooms Per Area</h3>
        <table>
          <thead>
            <tr>
              <th>Country</th>
              <th>City</th>
              <th>Available Rooms</th>
            </tr>
          </thead>
          <tbody>
            {availableRooms.map(area => (
              <tr key={`${area.Country}-${area.City}`}>
                <td>{area.Country}</td>
                <td>{area.City}</td>
                <td>{area.AvailableRooms}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* View 2: Aggregated Capacity of All Rooms of a Specific Hotel */}
      <section>
        <h3>Hotel Capacity</h3>
        <select value={selectedHotelId} onChange={e => setSelectedHotelId(e.target.value)}>
          <option value="">Select a Hotel</option>
          {hotels.map(hotel => (
            <option key={hotel.Hotel_ID} value={hotel.Hotel_ID}>{hotel.Name}</option>
          ))}
        </select>
        {hotelCapacity && (
          <div>
            <strong>Total Capacity:</strong> {hotelCapacity.TotalCapacity}
          </div>
        )}
      </section>

      {/* Add other meaningful views here as needed */}

    </div>
  );
};

export default HotelDirectory;
