import React, { useState } from 'react';

function Search() {
  const [searchParams, setSearchParams] = useState({
    startDate: '',
    endDate: '',
    capacity: '',
    area: '',
    hotelChain: '',
    category: '',
    priceRange: '',
  });

  const [results, setResults] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchParams(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Replace the URL with your actual search endpoint
    fetch('http://localhost:3001/api/search', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(searchParams),
    })
      .then(response => response.json())
      .then(data => {
        console.log('Search results:', data);
        setResults(data); // Assuming the data is the array of results
      })
      .catch(error => {
        console.error('Error fetching search results:', error);
      });
  };

  return (
    <div>
      <h2>Room Search</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Start Date:</label>
          <input
            type="date"
            name="startDate"
            value={searchParams.startDate}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>End Date:</label>
          <input
            type="date"
            name="endDate"
            value={searchParams.endDate}
            onChange={handleInputChange}
            required
          />
        </div>
        {/* Add more input fields for each search parameter as needed */}
        <button type="submit">Search</button>
      </form>
      <div>
        <h3>Search Results</h3>
        {results.length > 0 ? (
          results.map((result) => (
            <div key={result.id}>
              {/* Replace these paragraphs with actual result properties */}
              <p>Name: {result.name}</p>
              <p>Location: {result.location}</p>
              {/* Add more details about the result */}
            </div>
          ))
        ) : (
          <p>No results found</p>
        )}
      </div>
    </div>
  );
}

export default Search;
