import React, { useState } from 'react';

function Search() {
  const [rooms, setRooms] = useState([]);
  const [checkin, setCheckin] = useState('');
  const [checkout, setCheckout] = useState('');
  const [capacity, setCapacity] = useState('');

  const handleSearch = () => {
    if (!checkin || !checkout) {
      alert('Please select both check-in and check-out dates.');
      return;
    }

    if (new Date(checkin) > new Date(checkout)) {
      alert('Check-in date cannot be after check-out date.');
      return;
    }

    const url = new URL('http://localhost:3001/search');
    url.searchParams.append('checkin', checkin);
    url.searchParams.append('checkout', checkout);
    if (capacity) url.searchParams.append('capacity', capacity);

    fetch(url)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setRooms(data);
      })
      .catch(error => {
        console.error('Error fetching available rooms:', error);
      });
  };

  // Styles
  const containerStyle = { padding: '20px', maxWidth: '800px', margin: '0 auto' };
  const buttonStyle = { backgroundColor: '#007bff', color: 'white', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', marginBottom: '20px' };
  const inputStyle = { padding: '10px', margin: '0 10px 20px 0', borderRadius: '5px', border: '1px solid #ddd' };
  const roomStyle = { border: '1px solid #ddd', borderRadius: '5px', padding: '15px', marginBottom: '10px', backgroundColor: '#f9f9f9' };
  const titleStyle = { textAlign: 'center', color: '#333' };

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>Room Search</h2>
      <input type="date" value={checkin} onChange={e => setCheckin(e.target.value)} style={inputStyle} />
      <input type="date" value={checkout} onChange={e => setCheckout(e.target.value)} style={inputStyle} />
      <input type="number" placeholder="Capacity" value={capacity} onChange={e => setCapacity(e.target.value)} style={inputStyle} />
      <button onClick={handleSearch} style={buttonStyle}>Search Available Rooms</button>
      <div>
{rooms.map(room => (
  <div key={room.Room_ID} style={roomStyle}>
    <h3>{room.Name}</h3>
    <p>{room.Street}, {room.City}, {room.Country}</p>
    <p><strong>Price:</strong> ${room.Price}</p>
    <p><strong>Amenities:</strong> {room.Amenities}</p>
    <p><strong>Capacity:</strong> {room.Capacity}</p>
    {/* Only display Sea View if it's offered */}
    {room.Sea_View && <p><strong>Sea View:</strong> Yes</p>}
    {/* Only display Mountain View if it's offered */}
    {room.Mountain_View && <p><strong>Mountain View:</strong> Yes</p>}
    {/* Only display Extendable if it's offered */}
    {room.Extendable && <p><strong>Extendable:</strong> Yes</p>}
    <p><strong>Damage Status:</strong> {room.Damage_Status ? 'Yes' : 'No'}</p>
  </div>
))}

      </div>
    </div>
  );
}

export default Search;
