import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function BookingComponent() {
  const location = useLocation();
  const navigate = useNavigate();
  const { selectedRoom } = location.state || {};

  const [customer, setCustomer] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '', // Added password to the state
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCustomer(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  const submitBooking = () => {
    console.log('Submitting booking for:', customer);
    // Implement the logic to submit the booking details
    navigate('/booking-confirmation'); // Adjust the route as needed
  };

  if (!selectedRoom) {
    return <p>No room selected for booking.</p>;
  }

  return (
    <div>
      <h2>Booking for {selectedRoom.Name}</h2> {/* Adjust according to your data structure */}
      <p>{selectedRoom.Street}, {selectedRoom.City}, {selectedRoom.Country}</p>
      <form onSubmit={e => {
        e.preventDefault();
        submitBooking();
      }}>
        <input
          name="email"
          value={customer.email}
          onChange={handleChange}
          placeholder="Email"
        />
        <input
          type="password" // Ensure to use type="password" for the password input
          name="password"
          value={customer.password}
          onChange={handleChange}
          placeholder="Password"
        />
        <button type="submit">Submit Booking</button>
      </form>
    </div>
  );
}

export default BookingComponent;
