import React, { useState } from 'react';

function Search() {
  const [rooms, setRooms] = useState([]);

  const handleSearch = () => {
    // Update the URL to match your server's address and endpoint
    fetch('http://localhost:3001/api/search')
      .then(response => response.json())
      .then(data => {
        setRooms(data);
      })
      .catch(error => console.error('Error fetching rooms:', error));
  };

  return (
    <div>
      <h2>Room Search</h2>
      <button onClick={handleSearch}>Search</button>
      <div>
      {rooms.map(room => (
  <div key={room.Room_ID}>
    <p>Room ID: {room.Room_ID}</p>
    <p>Hotel ID: {room.Hotel_ID}</p>
    <p>Price: ${room.Price.toFixed(2)}</p>
    <p>Amenities: {room.Amenities}</p>
    <p>Capacity: {room.Capacity}</p>
    <p>Sea View: {room.Sea_View ? 'Yes' : 'No'}</p>
    <p>Mountain View: {room.Mountain_View ? 'Yes' : 'No'}</p>
    <p>Extendable: {room.Extendable ? 'Yes' : 'No'}</p>
    <p>Damage Status: {room.Damage_Status ? 'Yes' : 'No'}</p>
  </div>
))}

      </div>
    </div>
  );
}

export default Search;
