import React, { useState } from 'react';

function Search() {
  const [rooms, setRooms] = useState([]);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const handleSearch = () => {
    // Construct the search query with start and end dates
    const queryParams = new URLSearchParams({ startDate, endDate }).toString();
    const searchUrl = `http://localhost:3001/search/available?${queryParams}`;

    fetch(searchUrl)
      .then(response => response.json())
      .then(data => {
        setRooms(data);
        console.log('Rooms data:', data);
      })
      .catch(error => {
        console.error('Error fetching rooms:', error);
      });
  };

  // Styles
  const containerStyle = {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
  };

  const buttonStyle = {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    marginBottom: '20px',
  };

  const roomStyle = {
    border: '1px solid #ddd',
    borderRadius: '5px',
    padding: '15px',
    marginBottom: '10px',
    backgroundColor: '#f9f9f9',
  };

  const titleStyle = {
    textAlign: 'center',
    color: '#333',
  };

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>Room Search</h2>
      <div>
        <input 
          type="date" 
          value={startDate} 
          onChange={e => setStartDate(e.target.value)} 
          placeholder="Start Date"
        />
        <input 
          type="date" 
          value={endDate} 
          onChange={e => setEndDate(e.target.value)} 
          placeholder="End Date"
        />
      </div>
      <button onClick={handleSearch} style={buttonStyle}>Search</button>
      <div>
        {rooms.length > 0 ? rooms.map(room => (
          <div key={room.Room_ID} style={roomStyle}>
            <p><strong>Room ID:</strong> {room.Room_ID}</p>
            <p><strong>Hotel ID:</strong> {room.Hotel_ID}</p>
            <p><strong>Price:</strong> ${room.Price}</p>
            <p><strong>Amenities:</strong> {room.Amenities}</p>
            <p><strong>Capacity:</strong> {room.Capacity}</p>
            <p><strong>Sea View:</strong> {room.Sea_View ? 'Yes' : 'No'}</p>
            <p><strong>Mountain View:</strong> {room.Mountain_View ? 'Yes' : 'No'}</p>
            <p><strong>Extendable:</strong> {room.Extendable ? 'Yes' : 'No'}</p>
            <p><strong>Damage Status:</strong> {room.Damage_Status ? 'Yes' : 'No'}</p>
          </div>
        )) : <p>No rooms available for the selected dates.</p>}
      </div>
    </div>
  );
}

export default Search;
