import React, { useState } from 'react';

function Search() {
  const [rooms, setRooms] = useState([]);
  const [checkin, setCheckin] = useState('');
  const [checkout, setCheckout] = useState('');
  const [capacity, setCapacity] = useState(''); // Added state for capacity

  const handleSearch = () => {
    // Check if either check-in or check-out dates are not filled out
    if (!checkin || !checkout) {
      alert('Please select both check-in and check-out dates.');
      return;
    }

    // Continue with the previous validation to ensure check-in date is not after check-out date
    if (new Date(checkin) > new Date(checkout)) {
      alert('Check-in date cannot be after check-out date.');
      return;
    }

    // Construct the URL with query parameters for the search
    const url = new URL('http://localhost:3001/search');
    url.searchParams.append('checkin', checkin);
    url.searchParams.append('checkout', checkout);
    if (capacity) url.searchParams.append('capacity', capacity); // Append capacity to the query

    // Fetch available rooms from the server
    fetch(url)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setRooms(data);
      })
      .catch(error => {
        console.error('Error fetching available rooms:', error);
      });
  };

  // Styles
  const containerStyle = {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
  };

  const buttonStyle = {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    marginBottom: '20px',
  };

  const inputStyle = {
    padding: '10px',
    margin: '0 10px 20px 0',
    borderRadius: '5px',
    border: '1px solid #ddd',
  };

  const roomStyle = {
    border: '1px solid #ddd',
    borderRadius: '5px',
    padding: '15px',
    marginBottom: '10px',
    backgroundColor: '#f9f9f9',
  };

  const titleStyle = {
    textAlign: 'center',
    color: '#333',
  };

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>Room Search</h2>
      <input
        type="date"
        value={checkin}
        onChange={e => setCheckin(e.target.value)}
        style={inputStyle}
      />
      <input
        type="date"
        value={checkout}
        onChange={e => setCheckout(e.target.value)}
        style={inputStyle}
      />
      <input // New input for capacity
        type="number"
        placeholder="Capacity"
        value={capacity}
        onChange={e => setCapacity(e.target.value)}
        style={inputStyle}
      />
      <button onClick={handleSearch} style={buttonStyle}>Search Available Rooms</button>
      // Inside the Search function component...
<div>
  {rooms.map(room => (
    <div key={room.Room_ID} style={roomStyle}>
      <h3>{room.Name}</h3> {/* Display hotel name */}
      <p>{room.Street}, {room.City}, {room.Country}</p> {/* Display address */}
      <p><strong>Price:</strong> ${room.Price}</p>
      <p><strong>Amenities:</strong> {room.Amenities}</p>
      <p><strong>Capacity:</strong> {room.Capacity}</p>
      {room.Sea_View && <p><strong>Sea View:</strong> Yes</p>} {/* Conditionally displayed */}
      {room.Mountain_View && <p><strong>Mountain View:</strong> Yes</p>} {/* Conditionally displayed */}
      {room.Extendable && <p><strong>Extendable:</strong> Yes</p>} {/* Conditionally displayed */}
      {room.Damage_Status && <p><strong>Damage Status:</strong> Yes</p>} {/* Assuming you want to display Damage Status only if 'Yes' */}
    </div>
  ))}
</div>

    </div>
  );
}

export default Search;
