import React, { useState } from 'react';
import './Management.css'; // Make sure the path to your CSS file is correct

function Management() {
  const [employee, setEmployee] = useState({ email: '', password: '', hotelID: '' });
  const [rooms, setRooms] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [activeView, setActiveView] = useState(''); // Possible values: '', 'rooms', 'bookings'

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployee({ ...employee, [name]: value });
  };

  const authenticateEmployee = () => {
    fetch('http://localhost:3001/verify-employee', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        email: employee.email,
        password: employee.password
      }),
    })
    .then(response => response.json())
    .then(data => {
      if (data.isValid) {
        setIsAuthenticated(true);
        setEmployee({ ...employee, hotelID: data.hotelID });
      } else {
        alert('Authentication failed. Please check your credentials and try again.');
      }
    })
    .catch(error => {
      console.error('Authentication error:', error);
    });
  };

  const fetchRooms = () => {
    fetch(`http://localhost:3001/rooms-by-hotel?hotelId=${employee.hotelID}`)
    .then(response => response.json())
    .then(data => {
      setRooms(data);
      setActiveView('rooms');
    })
    .catch(error => console.error('Error fetching rooms:', error));
  };

  const fetchBookings = () => {
    fetch(`http://localhost:3001/bookings-by-hotel?hotelId=${employee.hotelID}`) // This endpoint needs to be implemented in your backend
    .then(response => response.json())
    .then(data => {
      setBookings(data);
      setActiveView('bookings');
    })
    .catch(error => console.error('Error fetching bookings:', error));
  };

  return (
    <div className="container">
      {!isAuthenticated ? (
        <>
          <h2 className="form-title">Employee Sign In</h2>
          <form onSubmit={e => {
            e.preventDefault();
            authenticateEmployee();
          }}>
            <input
              name="email"
              value={employee.email}
              onChange={handleChange}
              placeholder="Email"
              className="form-input"
            />
            <input
              type="password"
              name="password"
              value={employee.password}
              onChange={handleChange}
              placeholder="Password"
              className="form-input"
            />
            <div className="button-container">
              <button type="submit" className="form-button">Sign In</button>
            </div>
          </form>
        </>
      ) : (
        <>
          <button onClick={fetchRooms}>View Rooms</button>
          <button onClick={fetchBookings}>View Bookings</button>
          {activeView === 'rooms' && rooms.length > 0 ? (
            <>
              <h2>Rooms in Your Hotel</h2>
              {rooms.map(room => (
                <div key={room.Room_ID} className="room-container">
                  {/* Display room details here */}
                  <p>Room ID: {room.Room_ID}</p>
                  <p>Price: {room.Price}</p>
                  {/* Include other room details as needed */}
                </div>
              ))}
            </>
          ) : activeView === 'bookings' && bookings.length > 0 ? (
            <>
              <h2>Bookings</h2>
              {bookings.map(booking => (
                <div key={booking.Booking_ID} className="booking-container">
                  {/* Display booking details here */}
                  <p>Booking ID: {booking.Booking_ID}</p>
                  {/* Include other booking details as needed */}
                </div>
              ))}
            </>
          ) : (
            <p>No {activeView} found.</p>
          )}
        </>
      )}
    </div>
  );
}

export default Management;
