import React, { useState } from 'react';
import './SignUpForm.css';

function Management() {
  const [employee, setEmployee] = useState({ email: '', password: '', hotelID: '' });
  const [rooms, setRooms] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployee(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  const authenticateEmployee = () => {
    fetch('http://localhost:3001/verify-employee', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        email: employee.email,
        password: employee.password
      }),
    })
    .then(response => {
      if (!response.ok) throw new Error('Invalid credentials');
      return response.json();
    })
    .then(data => {
      if (data.isValid) {
        setIsAuthenticated(true);
        setEmployee(prevState => ({
          ...prevState,
          hotelID: data.hotelID
        }));
        fetchRooms(data.hotelID);
      } else {
        alert('Authentication failed. Please check your credentials and try again.');
      }
    })
    .catch(error => {
      console.error('Authentication error:', error);
    });
  };

  const fetchRooms = (hotelID) => {
    fetch(`http://localhost:3001/rooms-by-hotel?hotelId=${hotelID}`)
    .then(response => response.json())
    .then(data => {
      setRooms(data);
    })
    .catch(error => console.error('Error fetching rooms:', error));
  };

  return (
    <div className="container">
      {!isAuthenticated ? (
        <>
          <h2 className="form-title">Employee Sign In</h2>
          <form onSubmit={e => {
            e.preventDefault();
            authenticateEmployee();
          }}>
            <input
              name="email"
              value={employee.email}
              onChange={handleChange}
              placeholder="Email"
              className="form-input"
            />
            <input
              type="password"
              name="password"
              value={employee.password}
              onChange={handleChange}
              placeholder="Password"
              className="form-input"
            />
            <div className="button-container">
              <button type="submit" className="form-button">Sign In</button>
            </div>
          </form>
        </>
      ) : (
        <div>
          <h2>Rooms in Your Hotel</h2>
          {rooms.length > 0 ? (
            rooms.map(room => (
              <div key={room.Room_ID} style={{ margin: '20px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
                <p>Room ID: {room.Room_ID}</p>
                <p>Price: ${room.Price}</p>
                <p>Capacity: {room.Capacity}</p>
                <p>Amenities: {room.Amenities}</p>
                <p>Sea View: {room.Sea_View ? 'Yes' : 'No'}</p>
                <p>Mountain View: {room.Mountain_View ? 'Yes' : 'No'}</p>
                <p>Extendable: {room.Extendable ? 'Yes' : 'No'}</p>
                <p>Damage Status: {room.Damage_Status}</p>
              </div>
            ))
          ) : (
            <p>No rooms found.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default Management;
