import React, { useState } from 'react';

function Search() {
  const [rooms, setRooms] = useState([]);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const handleSearch = () => {
    // We'll call the /search/available endpoint with the startDate and endDate parameters
    fetch(`http://localhost:3001/search/available?startDate=${startDate}&endDate=${endDate}`)
      .then(response => response.json())
      .then(data => {
        setRooms(data);
        console.log('Available rooms data:', data);
      })
      .catch(error => {
        console.error('Error fetching available rooms:', error);
      });
  };

  // ...rest of your component

  // Add date inputs to your component's return statement
  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h2 style={{ textAlign: 'center', color: '#333' }}>Room Search</h2>
      <div>
        <label htmlFor="start-date">Start Date:</label>
        <input
          id="start-date"
          type="date"
          value={startDate}
          onChange={e => setStartDate(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="end-date">End Date:</label>
        <input
          id="end-date"
          type="date"
          value={endDate}
          onChange={e => setEndDate(e.target.value)}
        />
      </div>
      <button onClick={handleSearch} style={{ backgroundColor: '#007bff', color: 'white', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', marginBottom: '20px' }}>
        Search
      </button>
      <div>
        {rooms.map(room => (
          <div key={room.Room_ID} style={{ border: '1px solid #ddd', borderRadius: '5px', padding: '15px', marginBottom: '10px', backgroundColor: '#f9f9f9' }}>
            {/* ...display room details */}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Search;
