import React, { useState } from 'react';

function Search() {
  const [rooms, setRooms] = useState([]);
  const [checkin, setCheckin] = useState('');
  const [checkout, setCheckout] = useState('');

  const handleSearch = () => {
    if (!checkin || !checkout) {
      console.error('Please select both check-in and check-out dates.');
      return;
    }

    const url = new URL('http://localhost:3001/search');
    url.search = new URLSearchParams({ checkin, checkout }).toString();

    fetch(url)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setRooms(data);
      })
      .catch(error => {
        console.error('Error fetching available rooms:', error);
      });
  };

  // Styles omitted for brevity. Add your CSS or inline styles as needed.

  return (
    <div>
      <h2>Room Search</h2>
      <input
        type="date"
        value={checkin}
        onChange={e => setCheckin(e.target.value)}
      />
      <input
        type="date"
        value={checkout}
        onChange={e => setCheckout(e.target.value)}
      />
      <button onClick={handleSearch}>Search Available Rooms</button>
      <div>
        {rooms.map(room => (
          <div key={room.Room_ID}>
            <p>Room ID: {room.Room_ID}</p>
            <p>Hotel ID: {room.Hotel_ID}</p>
            <p>Price: ${room.Price}</p>
            {/* Other room details */}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Search;
